import React, {useState} from 'react';
import './App.css';
import Preview from './components/Preview';
import Message from './components/Message';
import NoteContainer from './components/Notes/NotesContainer';
import NotesList from './components/Notes/NotesList';
import Note from './components/Notes/Note';

function App() {

  const [notes,setNotes]= useState([]);
  const [title,setTitle]= useState('');
  const [content,setContent]= useState('');
  const [selectedNote,setSelectedNote]= useState(null);
  const [creating,setCreating]= useState(false);
  const [editing,setEditing]= useState(false);


  // تغير عنوان الملاحظة

  const changeTitleHandler = (event) =>{
    setTitle(event.target.value);
  }

  // تغير محتوى الملاحظة
  const changeContentHandler = (event) =>{
    setContent(event.target.value);
  }

  // حفظ الملاحظه
  const saveNoteHandler = () =>{
    const note ={
      id: new Date(),
      title: title,
      content: content
    }
    // تحديث الملاحظه
    const updateNotes = [...notes, note];
    setNotes(updateNotes);
    setCreating(false);
    setSelectedNote(note.id);
    setTitle('');
    setContent('');
  }

  // إختيار ملاحظة
  const selectNoteHandler = noteId => {
    setSelectedNote(noteId);
  }

  // الانتقال الى وضع تعديل ملاحطة
  const editNoteHandler =() => {
    const note = notes.find(note => note.id === selectedNote);
    setEditing(true);
    setTitle(note.title);
    setContent(note.content);
  }
  // تعديل ملاحظة
  const updateNoteHandler = () => {
    const updateNotes = [...notes];
    const noteIndex = notes.findIndex(note => note.id === selectedNote);
    updateNotes[noteIndex] ={
      id: selectedNote,
      title: title,
      content: content
    };
    setNotes (updateNotes);
    setEditing(false);
    setTitle('');
    setContent('');
  }

  // الانتقال الى صفحة إضافة ملاحظة 
  const addNoteHandler = () =>{
    setCreating(true);
    setEditing(false);
    setTitle('');
    setContent('');
  }

  const getAddNote = () => {
    return (
      <div>
        <h2>إضافة ملاحظة جديدة</h2>
        <div>
          <input
            type="text"
            name="title"
            className="form-input mb-30"
            placeholder="العنوان"
            value={title}
            onChange={changeTitleHandler}
          />

          <textarea
            rows="10"
            name="content"
            className="form-input"
            placeholder="النص"
            value={content}
            onChange={changeContentHandler}
          />

          <a href="#" className="button green" onClick={saveNoteHandler}>
            حفظ
          </a>
        </div>
      </div>
    );
  };

  const getPreview = () => {

    if(notes.length === 0){
      return <Message title="لايوجد ملاحظة " />
    }

    if (!selectedNote){
      return <Message title="الرجاء إختيار ملاحظة" />
    }

    const note = notes.find(note => {
      return note.id === selectedNote;
    });

    let noteDisplay =(
      <div>
        <h2>{note.title}</h2>
        <p>{note.content}</p>
      </div>
    )

    if(editing){
      noteDisplay =(
        <div>
            <h2>تعديل ملاحظة</h2>
            <form>
            <input
              type="text"
              name="title"
              className="form-input mb-30"
              placeholder="العنوان"
              value={title}
              onChange={changeTitleHandler}
            />

            <textarea
              rows="10"
              name="content"
              className="form-input"
              placeholder="النص"
              value={content}
              onChange={changeContentHandler}
            />
             <a href="#" className="button green" onClick={updateNoteHandler}>
            تعديل
          </a>
          </form>
        </div>
      );
    }
    return (
      <div>
        {!editing &&
        <div className="note-operations">
        <a href="#" onClick={editNoteHandler}>
          <i className="fa fa-pencil-alt" />
        </a>
        <a href="#">
          <i className="fa fa-trash" />
        </a>
      </div>
        }

        {noteDisplay}
        </div>
    );
  };


  return (
    <div className="App">
      <NoteContainer>
        <NotesList>
          {notes.map(note =>
          <Note
           key={note.id}
            title={note.title}
            noteClicked={() => selectNoteHandler(note.id)}
             active={selectedNote ===note.id}
             />
          )}
        </NotesList>
        <button className="add-btn" onClick={addNoteHandler}>+</button>
      </NoteContainer>
      <Preview>
      {creating ? getAddNote() : getPreview()}
      </Preview>
    </div>
  );
}

export default App;
